package Data;import java.util.ArrayList;import java.util.List;import java.util.Queue;import java.util.concurrent.LinkedBlockingQueue;// 对话上下文管理器（使用队列）public class DialogueContext {    private final Queue<Message> contextQueue;    private final int maxContextSize;    public DialogueContext(int maxSize) {        this.contextQueue = new LinkedBlockingQueue<>(maxSize);        this.maxContextSize = maxSize;    }    // 添加上下文    public void addToContext(Message message) {        if (contextQueue.size() >= maxContextSize) {            contextQueue.poll(); // 移除最老的消息        }        contextQueue.offer(message);    }    // 获取上下文    public List<Message> getContext() {        return new ArrayList<>(contextQueue);    }    // 打印当前上下文    public void printContext() {        System.out.println("=== 当前对话上下文 ===");        for (Message msg : contextQueue) {            System.out.println(msg);        }        System.out.println("====================");    }    public void clearContext() {        contextQueue.clear();    }}