package Data;/** * 双向链表节点 * 用于构建对话历史的双向链表结构，每个节点包含一条消息及其前后节点的引用 */public class DialogueNode {    /**     * 节点存储的消息对象     */    public Message message;        /**     * 指向前一个节点的引用     */    public DialogueNode prev;        /**     * 指向后一个节点的引用     */    public DialogueNode next;    /**     * 构造函数，创建一个新的对话节点     *      * @param message 要存储在节点中的消息对象     */    public DialogueNode(Message message) {        this.message = message;    }}