package Data;import java.util.List;/** * NLTK实体识别结果类 * 用于存储NLTK自然语言处理库识别出的实体信息列表 */public class EntitiesNLTK{    /**     * 实体列表，包含所有识别出的实体信息     */    private List<EntityNLTK> entities;    /**     * 无参构造函数，创建一个空的实体识别结果对象     */    public EntitiesNLTK() {    }    /**     * 获取识别出的实体列表     *      * @return 实体列表     */    public List<EntityNLTK> getEntities() {        return entities;    }    /**     * 设置识别出的实体列表     *      * @param entities 要设置的实体列表     */    public void setEntities(List<EntityNLTK> entities) {        this.entities = entities;    }    /**     * 返回对象的字符串表示形式     *      * @return 包含实体信息的字符串     */    @Override    public String toString() {        return "EntitiesNLTK{" +                "entities=" + entities +                '}';    }}