package Data;/** * NLTK实体类 * 用于存储NLTK自然语言处理库识别出的单个实体信息 * 包括实体文本内容和实体类型 */public class EntityNLTK{    /**     * 实体的文本内容     */    private String text;        /**     * 实体的类型（如人名、地名、组织名等）     */    private String type;    /**     * 无参构造函数，创建一个空的实体对象     */    public EntityNLTK() {    }    /**     * 获取实体的文本内容     *      * @return 实体文本     */    public String getText() {        return text;    }    /**     * 获取实体的类型     *      * @return 实体类型     */    public String getType() {        return type;    }    /**     * 设置实体的文本内容     *      * @param text 要设置的实体文本     */    public void setText(String text) {        this.text = text;    }    /**     * 设置实体的类型     *      * @param type 要设置的实体类型     */    public void setType(String type) {        this.type = type;    }}