package Data;import java.util.List;/** * NLTK意图识别结果类 * 用于存储NLTK自然语言处理库识别出的用户输入意图信息 * 包括识别状态、意图列表和置信度等信息 */public class IntentNLTK {    /**     * 意图识别状态，表示识别是否成功     */    private String status;        /**     * 识别出的意图列表，按置信度排序     */    private List<String> intents;        /**     * 识别的置信度，表示识别结果的可信程度     */    private double confidence;    /**     * 无参构造函数，创建一个空的意图识别结果对象     */    public IntentNLTK() {    }    /**     * 获取意图识别状态     *      * @return 意图识别状态字符串     */    public String getStatus() {        return status;    }    /**     * 获取识别出的意图列表     *      * @return 意图列表     */    public List<String> getIntents() {        return intents;    }    /**     * 获取识别置信度     *      * @return 置信度值（0.0-1.0之间）     */    public double getConfidence() {        return confidence;    }    /**     * 设置意图识别状态     *      * @param status 要设置的状态字符串     */    public void setStatus(String status) {        this.status = status;    }    /**     * 设置识别出的意图列表     *      * @param intents 要设置的意图列表     */    public void setIntents(List<String> intents) {        this.intents = intents;    }    /**     * 设置识别置信度     *      * @param confidence 要设置的置信度值     */    public void setConfidence(double confidence) {        this.confidence = confidence;    }    /**     * 返回对象的字符串表示形式     *      * @return 包含意图识别信息的字符串     */    @Override    public String toString() {        return "IntentNLTK{" +                "status='" + status + '\'' +                ", intents=" + intents +                ", confidence=" + confidence +                '}';    }}