package Data;import java.util.HashMap;import java.util.Map;// Trie树节点class TrieNode {    Map<Character, TrieNode> children;    boolean isEndOfWord;    String response; // 关键词对应的回复    public TrieNode() {        children = new HashMap<>();        isEndOfWord = false;        response = null;    }}// Trie树实现public class KeywordTrieTree {    private final TrieNode root;    public KeywordTrieTree() {        root = new TrieNode();    }    // 插入关键词和回复    public void insert(String keyword, String response) {        TrieNode current = root;        for (char ch : keyword.toLowerCase().toCharArray()) {            current = current.children.computeIfAbsent(ch, c -> new TrieNode());        }        current.isEndOfWord = true;        current.response = response;    }    // 搜索关键词    public String search(String text) {        String[] words = text.toLowerCase().split("\\s+");        for (String word : words) {            // 清理单词（移除标点）            String cleanWord = word.replaceAll("[^a-zA-Z0-9]", "");            TrieNode node = root;            for (char ch : cleanWord.toCharArray()) {                node = node.children.get(ch);                if (node == null) break;            }            if (node != null && node.isEndOfWord) {                return node.response;            }        }        return null;    }}