package Data;import IntentRecognizer.IntentRecognizer;// 消息类public class Message {    private final String sender;    //对象，user或bot    private final String content;   //聊天内容    private final IntentRecognizer.IntentAnalysisResult addition;  //备注，如NLP工具的分析记录    private final long timestamp;   //时间戳    public Message(String sender, String content) {        this.sender = sender;        this.content = content;        this.addition = null;        this.timestamp = System.currentTimeMillis();    }    public Message(String sender, String content, IntentRecognizer.IntentAnalysisResult addition) {        this.sender = sender;        this.content = content;        this.addition = addition;        this.timestamp = System.currentTimeMillis();    }    // getters    public String getSender() {        return sender;    }    public String getContent() {        return content;    }    public  IntentRecognizer.IntentAnalysisResult getAddition() {        return addition;    }    public long getTimestamp() {        return timestamp;    }    @Override    public String toString() {        if(addition == null) {            return String.format("%s: %s", sender, content);        }else {            return String.format("%s: %s\n%s", sender, content, addition.toString());        }    }}