package Data;/** * NLTK情感分析结果类 * 用于存储NLTK自然语言处理库进行情感分析后的结果 * 包括各种情感维度的数值和总体情感分类 */public class SentimentNLTK{    /**     * 复合情感得分，综合衡量情感极性(-1到1之间)     */    private float compound;        /**     * 积极情感得分(0到1之间)     */    private float positive;        /**     * 消极情感得分(0到1之间)     */    private float negative;        /**     * 中性情感得分(0到1之间)     */    private float neutral;        /**     * 总体情感分类，如"positive"、"negative"或"neutral"     */    private String sentiment = null;    /**     * 无参构造函数，创建一个空的情感分析结果对象     */    public SentimentNLTK() {    }    /**     * 获取积极情感得分     *      * @return 积极情感得分值(0到1之间)     */    public float getPositive() {        return positive;    }    /**     * 获取消极情感得分     *      * @return 消极情感得分值(0到1之间)     */    public float getNegative() {        return negative;    }    /**     * 获取中性情感得分     *      * @return 中性情感得分值(0到1之间)     */    public float getNeutral() {        return neutral;    }    /**     * 获取总体情感分类     *      * @return 情感分类字符串，如"positive"、"negative"或"neutral"     */    public String getSentiment() {        return sentiment;    }    /**     * 获取复合情感得分     *      * @return 复合情感得分值(-1到1之间)     */    public float getCompound() {        return compound;    }    /**     * 设置复合情感得分     *      * @param compound 要设置的复合情感得分值     */    public void setCompound(float compound) {        this.compound = compound;    }    /**     * 设置积极情感得分     *      * @param positive 要设置的积极情感得分值     */    public void setPositive(float positive) {        this.positive = positive;    }    /**     * 设置消极情感得分     *      * @param negative 要设置的消极情感得分值     */    public void setNegative(float negative) {        this.negative = negative;    }    /**     * 设置中性情感得分     *      * @param neutral 要设置的中性情感得分值     */    public void setNeutral(float neutral) {        this.neutral = neutral;    }    /**     * 设置总体情感分类     *      * @param sentiment 要设置的情感分类字符串     */    public void setSentiment(String sentiment) {        this.sentiment = sentiment;    }    /**     * 返回对象的字符串表示形式     *      * @return 包含所有情感分析结果的字符串     */    @Override    public String toString() {        return "SentimentNLTK{" +                "compound=" + compound +                ", positive=" + positive +                ", negative=" + negative +                ", neutral=" + neutral +                ", sentiment='" + sentiment + '\'' +                '}';    }}