package LLM;import io.reactivex.Flowable;/** * 模型处理器接口 * 定义了与大语言模型交互的基本方法，包括生成响应和流式响应功能 */public interface ModelHandler {    /**     * 生成模型响应     *      * @param userInput 用户输入的内容     * @return 模型生成的响应文本     * @throws Exception 模型调用过程中可能抛出的异常     */    String generateResponse(String userInput) throws Exception;        /**     * 生成模型响应（流式）     *      * @param userInput 用户输入的内容     * @return 模型生成的响应文本流     * @throws Exception 模型调用过程中可能抛出的异常     */    Flowable<String> generateResponseStream(String userInput) throws Exception;    /**     * 添加机器人消息到对话历史中     *      * @param fullResponse 机器人完整的响应内容     */    void addBotMessage(String fullResponse);}