package SimpleResponseGenerator;import java.util.*;// 中文处理工具类public class ChineseTextProcessor {    // 方法1：基于词典的最大正向匹配分词    public static List<String> forwardMaxMatch(String text, Set<String> dictionary) {        List<String> result = new ArrayList<>();        int maxWordLength = getMaxWordLength(dictionary);        int position = 0;        int textLength = text.length();        while (position < textLength) {            int len = Math.min(maxWordLength, textLength - position);            String word = null;            // 从最长可能词开始匹配            for (; len > 0; len--) {                String candidate = text.substring(position, position + len);                if (dictionary.contains(candidate)) {                    word = candidate;                    break;                }            }            if (word == null) {                // 单字成词                word = text.substring(position, position + 1);                len = 1;            }            result.add(word);            position += len;        }        return result;    }    // 方法2：简单按字符分割（用于基础功能）    public static List<String> characterSegmentation(String text) {        List<String> characters = new ArrayList<>();        for (int i = 0; i < text.length(); i++) {            characters.add(String.valueOf(text.charAt(i)));        }        return characters;    }    // 方法3：混合分词（字符+常见词）    public static List<String> hybridSegmentation(String text, Set<String> commonWords) {        List<String> result = new ArrayList<>();        int i = 0;        while (i < text.length()) {            boolean found = false;            // 尝试匹配2-4字词语            for (int len = 4; len >= 2; len--) {                if (i + len <= text.length()) {                    String candidate = text.substring(i, i + len);                    if (commonWords.contains(candidate)) {                        result.add(candidate);                        i += len;                        found = true;                        break;                    }                }            }            if (!found) {                // 单字分割                result.add(text.substring(i, i + 1));                i++;            }        }        return result;    }    private static int getMaxWordLength(Set<String> dictionary) {        return dictionary.stream()                .mapToInt(String::length)                .max()                .orElse(1);    }    // 中文标点符号处理    public static String removeChinesePunctuation(String text) {        return text.replaceAll("[\\pP\\p{Punct}]", "");    }    // 判断字符是否为中文    public static boolean isChineseCharacter(char c) {        Character.UnicodeBlock ub = Character.UnicodeBlock.of(c);        return ub == Character.UnicodeBlock.CJK_UNIFIED_IDEOGRAPHS                || ub == Character.UnicodeBlock.CJK_COMPATIBILITY_IDEOGRAPHS                || ub == Character.UnicodeBlock.CJK_UNIFIED_IDEOGRAPHS_EXTENSION_A;    }}