package SimpleResponseGenerator;import java.util.Collections;import java.util.HashSet;import java.util.List;import java.util.Set;// 简易中文分词器实现public class SimpleChineseSegmenter implements ChineseSegmenter {    private final Set<String> dictionary;    public SimpleChineseSegmenter() {        this.dictionary = loadBasicDictionary();    }    private Set<String> loadBasicDictionary() {        Set<String> dict = new HashSet<>();        // 常见中文词语        String[] commonWords = {                "你好", "谢谢", "请问", "帮助", "名字", "年龄", "时间", "天气",                "笑话", "再见", "对不起", "没关系", "开心", "悲伤", "问题",                "今天", "明天", "昨天", "现在", "时候", "什么", "为什么",                "怎么", "如何", "哪里", "谁", "我的", "你的", "他的", "她的"        };        Collections.addAll(dict, commonWords);        return dict;    }    @Override    public List<String> segment(String text) {        return ChineseTextProcessor.hybridSegmentation(text, dictionary);    }    // 添加自定义词语    public void addWord(String word) {        dictionary.add(word);    }}